import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ProductServiceService } from '../product-service.service';
import { ModelProduit } from '../Model/model-produit';

@Component({
  selector: 'app-ajouter-produit',
  templateUrl: './ajouter-produit.component.html',
  styleUrls: ['./ajouter-produit.component.css']
})
export class AjouterProduitComponent {

  constructor(public _service: ProductServiceService, private router: Router) {}

  produit = new ModelProduit();

  save()  {
    const formData = new FormData();
    formData.append('name', this.produit.name);
    formData.append('price', this.produit.price.toString());  // Ensure number is converted to string
    formData.append('quantity', this.produit.quantity.toString());  // Ensure number is converted to string

    this._service.createProduct(formData).subscribe(
        res => {
            console.log(res);
            this.router.navigate(['/listProduct']);
        },
        err => {
            console.log(err);
        }
    );
}}
<app-nav-bar></app-nav-bar>
<div class="container">
  <div class="row p-4">
    <div class="col-3">
    </div>

    <div class="col">
      <form #f="ngForm" (submit)="save()">
        <div class="mb-3">
          <label for="disabledTextInput" class="form-label">Product Name:</label>
          <input type="text" name="name" [(ngModel)]="produit.name" class="form-control m-1" />
        </div>
        <div class="mb-3">
          <label for="disabledTextInput" class="form-label">Product Price:</label>
          <input type="number" name="price" [(ngModel)]="produit.price" class="form-control m-1" />
        </div>
        <div class="mb-3">
          <label for="disabledTextInput" class="form-label">Product Quantity:</label>
          <input type="number" name="quantity" [(ngModel)]="produit.quantity" class="form-control m-1" />
        </div>
        <button class="btn form-control btn btn-primary">Create</button>
      </form>
    </div>

    <div class="col-3">
    </div>
  </div>
</div>
