import { Component } from '@angular/core';
import { ProductServiceService } from '../product-service.service';

@Component({
  selector: 'app-nav-bar',
  templateUrl: './nav-bar.component.html',
  styleUrls: ['./nav-bar.component.css']
})
export class NavBarComponent {
constructor(private _service:ProductServiceService){}
ProductName:string ="";



search() {
 this._service.searchInput = this.ProductName;
 //use the nest of the bahavior to notifies the subscriber 
 this._service.searchBarTouchedSubject.next(this.ProductName);
}
}
<nav class="navbar navbar-expand-lg bg-body-tertiary">
    <div class="container-fluid">
      <a class="navbar-brand" routerLink="/list">GESTION DE STOCKAGE</a>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarSupportedContent">
        <ul class="navbar-nav me-auto mb-2 mb-lg-0">
          <li class="nav-item">
            <a class="nav-link active" aria-current="page" routerLink="/listProduct">list Product</a>
          </li>
          <li class="nav-item">
            <a class="nav-link active" aria-current="page" routerLink="/AddProduct">Add Product</a>
          </li>
        </ul>
        <form class="d-flex" role="search" #s="ngForm" (submit)="search()">
          <input class="form-control me-2" name="ProductName" [(ngModel)]="ProductName" (input)="search()" type="search"
            placeholder="Search" aria-label="Search">
          <button class="btn btn-outline-success" type="submit">Search</button>
        </form>
      </div>
    </div>
  </nav>
