import { Component } from '@angular/core';
import { ProductServiceService } from '../product-service.service';
import { ActivatedRoute, Router} from '@angular/router';
import { ModelProduit } from '../Model/model-produit';

@Component({
  selector: 'app-details-produit',
  templateUrl: './details-produit.component.html',
  styleUrls: ['./details-produit.component.css']
})
export class DetailsProduitComponent {
  constructor( private _service:ProductServiceService, private r:ActivatedRoute,private router:Router){
  }
  produit:any;
  idprod:any;

  ngOnInit(): void {
    this.idprod=this.r.snapshot.paramMap.get('id');
    this._service.getProductById(this.r.snapshot.paramMap.get('id')).subscribe(
    res=>{
        this.produit=res;
      },err=>{
        console.log(err);

      }
    )
  }

  delete(id:any){
    this._service.delete(id).subscribe(
      res=>{
        console.log(id);
        //get the new update 
        this.router.navigate(['/']);
      },err=>{
        console.log(err);

      }
    )
  }
}
<app-nav-bar></app-nav-bar>
<body>
  <div class="container">
    <div class="row justify-content-center">
      <div class="col-md-6">
        <div class="card">
          <div class="card-body text-center">

            <h5 class="card-title">{{produit.name}}</h5>
            <h6 class="card-subtitle mb-2 text-muted">{{produit.price}} DT</h6>
            <p class="card-text">Quantity: {{produit.quantity}}</p>
            <div class="d-flex justify-content-center">
              <button class="btn btn-danger mx-1" (click)="delete(produit._id)">Delete</button>
              <button class="btn btn-primary mx-1" >Update</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</body>
