import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { ProductServiceService } from '../product-service.service';
import { ModelProduit } from '../Model/model-produit';

@Component({
  selector: 'app-ajouter-produit',
  templateUrl: './ajouter-produit.component.html',
  styleUrls: ['./ajouter-produit.component.css']
})
export class AjouterProduitComponent {
  produit = new ModelProduit();
  selectedFile: File | null = null;

  constructor(public _service: ProductServiceService, private router: Router) {}

  handleFileInput(event: Event) {
    const input = event.target as HTMLInputElement;  
    if (input.files && input.files.length > 0) {
      this.selectedFile = input.files[0];
    }
  }



  save() {
    const formData = new FormData();
    formData.append('name', this.produit.name);
    formData.append('price', this.produit.price.toString());
    formData.append('quantity', this.produit.quantity.toString());
    if (this.selectedFile) {
      formData.append('image', this.selectedFile, this.selectedFile.name);
    }

    this._service.createProduct(formData).subscribe(
      res => {
        console.log(res);
        this.router.navigate(['/listProduct']);
      },
      err => {
        console.log(err);
      }
    );
  }
}
<div class="container">
  <div class="row p-4">
    <div class="col-3">
    </div>

    <div class="col">
      <form #f="ngForm" (ngSubmit)="save()" enctype="multipart/form-data">
        <div class="mb-3">
          <label for="productName" class="form-label">Product Name:</label>
          <input type="text" id="productName" name="name" [(ngModel)]="produit.name" class="form-control m-1" required />
        </div>
        <div class="mb-3">
          <label for="productPrice" class="form-label">Product Price:</label>
          <input type="number" id="productPrice" name="price" [(ngModel)]="produit.price" class="form-control m-1" required />
        </div>
        <div class="mb-3">
          <label for="productQuantity" class="form-label">Product Quantity:</label>
          <input type="number" id="productQuantity" name="quantity" [(ngModel)]="produit.quantity" class="form-control m-1" required />
        </div>
        <div class="mb-3">
          <label for="productImage" class="form-label">Product Image:</label>
          <input type="file" id="productImage" (change)="handleFileInput($event)" class="form-control m-1" />
        </div>
        <button class="btn btn-primary form-control">Create</button>
      </form>
    </div>

    <div class="col-3">
    </div>
  </div>
</div>
