import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { ModelProduit } from './Model/model-produit';
import { BehaviorSubject, Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ProductServiceService {

  private url = 'http://localhost:3000/api/';

  constructor(private http: HttpClient) { }

  searchBarTouchedSubject = new BehaviorSubject<String>("");
  searchBarTouched$ = this.searchBarTouchedSubject.asObservable();

  searchInput!: string;

  private processProducts(products: ModelProduit[]): ModelProduit[] {
    return products.map(product => {
      if (product.imageUrl) {
        product.imageUrl = `http://localhost:3000/${product.imageUrl}`;
      }
      return product;
    });
  }

  getProduit(): Observable<ModelProduit[]> {
    return this.http.get<ModelProduit[]>(this.url + 'GetProduct').pipe(
      map(products => this.processProducts(products))
    );
  }

  createProduct(prodData: FormData): Observable<any> {
    return this.http.post(this.url + 'AddProduct', prodData);
  }

  delete(id: any): Observable<any> {
    return this.http.delete(this.url + "id/" + id);
  }

  getProductById(id: any): Observable<ModelProduit> {
    return this.http.get<ModelProduit>(this.url + "id/" + id);
  }

  update(id: any, hero: any): Observable<any> {
    return this.http.put(this.url + "id/" + id, hero);
  }

  searchByName(ProductName: string): Observable<ModelProduit[]> {
    return this.http.get<ModelProduit[]>(`${this.url}search/${ProductName}`).pipe(
      map(products => this.processProducts(products))
    );
  }
}
